metaData.ecoCode.food    = {'0iO'};
metaData.ecoCode.gender  = {'Dg'};
metaData.ecoCode.reprod  = {'O'};

metaData.T_typical  = C2K(25); % K, body temp

metaData.data_0     = {'ab'; 'ap'; 'am'; 'Lb'; 'Li';  'Ri'};
