metaData.date_acc    = [2023 01 03];

%% set zero-variate data
data.ab = 15*7; units.ab = 'd'; label.ab = 'age at birth'; bibkey.ab = 'Spor1955';
  temp.ab = C2K(27); units.temp.ab = 'K'; label.temp.ab = 'temperature';
  comment.ab = '13-18 w';
data.tp = 3*365; units.tp = 'd'; label.tp = 'time since birth at puberty'; bibkey.tp = 'Spor1965';
  temp.tp = C2K(25); units.temp.tp = 'K'; label.temp.tp = 'temperature';
data.am = 20*365; units.am = 'd'; label.am = 'life span'; bibkey.am = 'Spor1965';
  temp.am = C2K(25); units.temp.am = 'K'; label.temp.am = 'temperature';

data.Lb = 3.6; units.Lb = 'cm'; label.Lb = 'SVL at birth'; bibkey.Lb = 'Spor1955';
  comment.Lb = '6 cm total length, coverted to SVL';
data.Li = 11; units.Li = 'cm'; label.Li = 'ultimate SVL'; bibkey.Li = 'Spor1965';
  comment.Li = '18.8 cm total length, coverted to SVL';

data.Ri = 6.5/365; units.Ri = '#/d'; label.Ri = 'ultimate reproduction rate'; bibkey.Ri = 'Spor1955';
  temp.Ri = C2K(25); units.temp.Ri = 'K'; label.temp.Ri = 'temperature';
  comment.Ri = '6-7 eggs per clutch, 1 clutch per yr';

