data.tL(:,2:3) = data.tL(:,2:3)*9/15; % convert total length to SVL
units.tL = {'d', 'cm'}; label.tL = {'time', 'SVL'};
temp.tL = C2K(25); units.temp.tL = 'K'; label.temp.tL = 'temperature';
bibkey.tL = {'Spor1955','Spor1965'}; treat.tL = {1, {'female','male'}};
comment.tL = 'Total length was converted to SVL by factor 9/15,  based on photo';

%% set weights for all real data
weights = setweights(data, []);
weights.tL = 10 * weights.tL;

%% set pseudodata and respective weights
[data, units, label, weights] = addpseudodata(data, units, label, weights);
weights.psd.v = 2 * weights.psd.v;

%% pack auxData and txtData for output
auxData.temp = temp;
auxData.treat = treat;
txtData.units = units;
txtData.label = label;
txtData.bibkey = bibkey;
txtData.comment = comment;

%% Group plots
