D2 = 'Temperature of 25C based on modelling using NicheMapR of bearded dragon body temperatures in the wild';
D3 = 'tp was ignorged because of large uncertainty, Wwp because of inconsistency with LWw data';
metaData.discussion = struct('D1',D1, 'D2',D2, 'D3',D3);

%% Facts
F1 = 'Specialized on feeding on the ant Iridomyrmes niger';
metaData.bibkey.F1 = 'PianPian1970'; 
metaData.facts = struct('F1',F1);

%% Links
metaData.links.id_CoL = '43WS4'; % Cat of Life
metaData.links.id_ITIS = '1055593'; % ITIS
