metaData.links.id_ReptileDB = 'genus=Moloch&species=horridus'; % ReptileDB
metaData.links.id_AnAge = 'Pogona_barbata'; % AnAge


%% References
bibkey = 'Wiki'; type = 'Misc'; bib = ...
'howpublished = {\url{http://en.wikipedia.org/wiki/Moloch_horridus}}';
